      const promises = [];
      for (let i = 0; i < 100; i++) {
        promises.push(api.post('/auth/login', validCredentials));
      }
      
      const results = await Promise.all(promises);
      const successful = results.filter(r => r.status === 200);
      expect(successful.length).toBeGreaterThan(95); // >95% success
    });
  });
});
